    public function setLang(string $lang): void{
        $this->currentLang = $lang;
    }

    public function get(string $key): string{
        return $this->translations[$this->currentLang][$key]
            ?? $this->translations[$this->defaultLang][$key]
            ?? "[$key]";
    }
